sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var whatneeded = g_form.getValue('u_what_needed');

    // Clear all of the choices from the What needed field choice list
    g_form.clearOptions('u_what_needed');

    switch (newValue) {
        case 'hr':
            // If the value of the Request type field is hr, add
            // one hr choices and other to the What needed field choice list
            g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
            g_form.addOption('u_what_needed', 'other', 'Other');
            break;
        case 'facilities':
            // If the value of the Request type field is facilities, add
            // one facilities choices and other to the What needed field
            // choice list
            g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
            g_form.addOption('u_what_needed', 'other', 'Other');
            break;
        case 'legal':
            // If the value of the Request type field is legal, add
            // one legal choices and other to the What needed field
            // choice list
            g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
            g_form.addOption('u_what_needed', 'other', 'Other');
            break;
        case 'your_choice':
            // If the value of the Request type field is yourchoice, add
            // two department choices and other to the What needed field
            // choice list
            g_form.addOption('u_what_needed', 'your_choice1', 'Your Department Choice 1');
            g_form.addOption('u_what_needed', 'your_choice2', 'Your Department Choice 2');
            g_form.addOption('u_what_needed', 'other', 'Other');
            break;
            // If the the value of the Request type field is TPS Report, add
            // only other to the What needed field
            // choice list
		case 'TPS Report':
            g_form.addOption('u_what_needed', 'other', 'Other');
            break;
		default:
            g_form.addOption('u_what_needed', 'other', 'Other');
    }

}
